import styled from "styled-components";

const SidebarWrapper = styled.div`
  width: 250px;
  height: 100vh;
  max-height : 100vh;
  overflow-y:  auto;
  background-color: #00202d;
  color: white;
  padding: 20px;
`;

const SidebarItem = styled.div`
  margin-bottom: 10px;
`;

const SidebarLink = styled.a`
  display: block;
  color: white;
  text-decoration: none;
  padding: 10px 0;
  font-size: 16px;
  cursor: pointer;

  &:hover {
    background-color: #555;
  }
`;

const SubItem = styled.div`
  margin-left: 20px;
  padding: 8px 0;
  font-size: 14px;
`;

const imgStyle = styled.img`
  height: 20px;
  width: 20px;
  margin-left: 10px;
`;
