import React, { useState } from 'react';

const Table = () => {
  const [tableData, setTableData] = useState([]);
  const [formData, setFormData] = useState({ nome: '', idade: '', cidade: '' });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSave = () => {
    const { nome, idade, cidade } = formData;
    if (nome.trim() !== '' && idade.trim() !== '' && cidade.trim() !== '') {
      const newItem = { nome, idade, cidade };
      setTableData([...tableData, newItem]);
      setFormData({ nome: '', idade: '', cidade: '' });
    }
  };

  return (
    <div style={{display:'flex', fontFamily: 'Arial, sans-serif', maxWidth: '100vh',margin: 'auto', flexDirection:'column',}}>
      <h2 style={{ textAlign: 'center',  marginTop: '20px'}}>Cadastro de Pessoas</h2>

      <div style={{display: 'flex', marginBottom: '20px', alignItems: 'center',  marginTop: '20px', }}>
        <label htmlFor="nome">Nome:</label>
        <input type="text" id="nome" name="nome" value={formData.nome} onChange={handleInputChange} />

        <label htmlFor="idade">Idade:</label>
        <input type="text" id="idade" name="idade" value={formData.idade} onChange={handleInputChange} />

        <label htmlFor="cidade">Cidade:</label>
        <input type="text" id="cidade" name="cidade" value={formData.cidade} onChange={handleInputChange} />

        <button onClick={handleSave} style={{ marginTop: '0px',  }}>Salvar</button>
      </div>

      <table style={{ width: '100%', borderCollapse: 'collapse', marginBottom: '20px' }}>
        <thead>
          <tr style={{ backgroundColor: '#f2f2f2', borderBottom: '3px solid #ddd'  }}>
            <th style={{ padding: '10px', textAlign: 'left' }}>Nome</th>
            <th style={{ padding: '10px', textAlign: 'left' }}>Idade</th>
            <th style={{ padding: '10px', textAlign: 'left' }}>Cidade</th>
          </tr>
        </thead>
        <tbody>
          {tableData.map((rowData, index) => (
            <tr key={index} style={{ borderBottom: '3px solid #ddd' }}>
              <td style={{ padding: '10px' }}>{rowData.nome}</td>
              <td style={{ padding: '10px' }}>{rowData.idade}</td>
              <td style={{ padding: '10px' }}>{rowData.cidade}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Table;
