import React, { useState } from 'react';
import styled from 'styled-components';

const SidebarWrapper = styled.div`
  width: 250px;
  height: 100vh;
  background-color: #00202d;
  color: white;
  padding: 20px;
`;

const SidebarItem = styled.div`
  margin-bottom: 10px;
`;

const SidebarLink = styled.a`
  display: block;
  color: white;
  text-decoration: none;
  padding: 10px 0;
  font-size: 16px;
  cursor: pointer;

  &:hover {
    background-color: #555;
  }
`;

const SubItem = styled.div`
  margin-left: 20px;
  padding: 8px 0;
  font-size: 14px;
`;

const Sidebar = () => {
  const [showSubItems, setShowSubItems] = useState({});

  const handleToggleSubItems = (item) => {
    setShowSubItems((prev) => ({
      ...prev,
      [item]: !prev[item],
    }));
  };

  return (
    <SidebarWrapper>
      <h2>Empresa</h2>
      <SidebarItem>
        <SidebarLink href="/">Dashboard</SidebarLink>
      </SidebarItem>
      <SidebarItem>
        <SidebarLink onClick={() => handleToggleSubItems('analytics')}>
          Analytics
        </SidebarLink>
        {showSubItems['analytics'] && (
          <>
            <SubItem>Subitem 1</SubItem>
            <SubItem>Subitem 2</SubItem>
          </>
        )}
      </SidebarItem>
      <SidebarItem>
        <SidebarLink onClick={() => handleToggleSubItems('settings')}>
          Settings
        </SidebarLink>
        {showSubItems['settings'] && (
          <>
            <SubItem>Subitem 1</SubItem>
            <SubItem>Subitem 2</SubItem>
          </>
        )}
      </SidebarItem>
      {/* Adicione mais itens e subitens conforme necessário */}
    </SidebarWrapper>
  );
};

export default Sidebar;
