// Sidebar.js
import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const SidebarWrapper = styled.div`
  width: ${(props) => (props.isOpen ? '250px' : '0')};
  height: 100vh;
  background-color: #00202d;
  color: white;
  padding: 20px;
  overflow-y: auto;
  position: fixed;
  top: 0;
  left: 0;
  transition: width 0.3s ease-in-out;
`;

const BurgerMenu = styled.div`
  position: fixed;
  top: 20px;
  left: 20px;
  cursor: pointer;
  z-index: 2;
  display: none; /* Ocultar por padrão */
  
  @media (max-width: 1000px) {
    display: block; /* Exibir quando a largura da tela for menor que 1000px */
  }
`;

const BurgerIcon = styled.div`
  width: 30px;
  height: 3px;
  background-color: white;
  margin: 6px 0;
  transition: 0.4s;
`;

const SidebarItem = styled.div`
  margin-bottom: 10px;
`;

const SidebarLink = styled.a`
  display: block;
  color: white;
  text-decoration: none;
  padding: 10px 0;
  font-size: 16px;
  cursor: pointer;

  &:hover {
    background-color: #555;
  }
`;

const SubItem = styled.div`
  margin-left: 20px;
  padding: 8px 0;
  font-size: 14px;
`;

const Sidebar = () => {
  const [showSubItems, setShowSubItems] = useState({});
  const [isOpen, setIsOpen] = useState(false);

  const handleToggleSubItems = (item) => {
    setShowSubItems((prev) => ({
      ...prev,
      [item]: !prev[item],
    }));
  };

  const handleToggleMenu = () => {
    setIsOpen(!isOpen);
  };

  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth > 1000) {
        setIsOpen(true);
      } else {
        setIsOpen(false);
      }
    };

    window.addEventListener('resize', handleResize);
    handleResize(); // Verifica o tamanho inicial da tela

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <>
      <BurgerMenu onClick={handleToggleMenu}>
        <BurgerIcon style={isOpen ? { transform: 'rotate(-45deg)' } : {}} />
        <BurgerIcon style={isOpen ? { opacity: '0' } : {}} />
        <BurgerIcon style={isOpen ? { transform: 'rotate(45deg)' } : {}} />
      </BurgerMenu>
      <SidebarWrapper isOpen={isOpen}>
        <h2>Empresa</h2>

        <SidebarItem>
          <SidebarLink onClick={() => handleToggleSubItems('analytics')}>
            Cadastro
          </SidebarLink>
          {showSubItems['analytics'] && (
            <>
              <SubItem>
                <SidebarLink href="/classe">Classe</SidebarLink>
              </SubItem>
              {/* Adicione mais subitens conforme necessário */}
            </>
          )}
        </SidebarItem>

        <SidebarItem>
          <SidebarLink href="/">Elástico</SidebarLink>
        </SidebarItem>
        <SidebarItem>
          <SidebarLink href="/">Agulha</SidebarLink>
        </SidebarItem>
        <SidebarItem>
          <SidebarLink href="/">Linha</SidebarLink>
        </SidebarItem>
        <SidebarItem>
          <SidebarLink href="/">Tecido</SidebarLink>
        </SidebarItem>
        <SidebarItem>
          <SidebarLink onClick={() => handleToggleSubItems('settings')}>
            Settings
          </SidebarLink>
          {showSubItems['settings'] && (
            <>
              <SubItem>Subitem 1</SubItem>
              <SubItem>Subitem 2</SubItem>
              {/* Adicione mais subitens conforme necessário */}
            </>
          )}
        </SidebarItem>
        <SidebarItem>
          <SidebarLink href="/">Logout</SidebarLink>
        </SidebarItem>
      </SidebarWrapper>
    </>
  );
};

export default Sidebar;
