import React, { useEffect, useState } from "react";
import Input from "../../components/Input";
import Select from "../../components/Select";
import Button from "../../components/Button";
import * as C from "./styles";
import { Link, useNavigate } from "react-router-dom";
import useAuth from "../../hooks/useAuth";
import routeApi from "../../env";

const Signup = () => {
  const [Usuário, setUsuário] = useState("");
  const [UsuárioConf, setUsuárioConf] = useState("");
  const [permissao, setPermissao] = useState(3);
  const [senha, setSenha] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleSignup = () => {
    if (!Usuário || !UsuárioConf || !permissao || !senha) {
      setError("Preencha todos os campos");
      return;
    } 
    else if (Usuário !== UsuárioConf) {
      setError("Os Usuários não são iguais");
      return;
    }

    /*
    const res = signup(Usuário, senha);

    if (res) {
      setError(res);
      return;
    }

    alert("Usuário cadatrado com sucesso!");
    navigate("/"); 
    */
  };

  enviarUsuario = async () =>{
    const response = await fetch('http://192.168.0.101:5000/usuarios', {
      method: 'POST',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({ username: usuário, senha: senha, id_permissao:permissao })
    })
    if(response.ok) {
      alert('Usuário cadastrado com sucesso!')
    }
  }

  return (
    <C.Container>
      
      <C.Content>
      <C.Label>CRIE SUA CONTA</C.Label>
        <Input
          type="Usuário"
          placeholder="Digite seu Usuário"
          value={Usuário}
          onChange={(e) => [setUsuário(e.target.value), setError("")]}
        />
        <Input
          type="Usuário"
          placeholder="Confirme seu Usuário"
          value={UsuárioConf}
          onChange={(e) => [setUsuárioConf(e.target.value), setError("")]}
        />
        <Input
          type="password"
          placeholder="Digite sua Senha"
          value={senha}
          onChange={(e) => [setSenha(e.target.value), setError("")]}
        />
        <C.labelError>{error}</C.labelError>
        <Button Text="Inscrever-se" onClick={handleSignup} />
        <C.LabelSignin>
          Já tem uma conta?
          <C.Strong>
            <Link to="/">&nbsp;Entre</Link>
          </C.Strong>
        </C.LabelSignin>
      </C.Content>
    </C.Container>
  );
};

export default Signup;
