// Sidebar.js
import React, { useState } from 'react';
import styled from 'styled-components';

const SidebarWrapper = styled.div`
  width: 250px;
  height: 100vh;
  background-color: #333;
  color: white;
  padding: 20px;
`;

const SidebarItem = styled.div`
  margin-bottom: 10px;
`;

const SidebarLink = styled.a`
  display: block;
  color: white;
  text-decoration: none;
  padding: 10px 0;
  font-size: 16px;
  cursor: pointer;

  &:hover {
    background-color: #555;
  }
`;

const SubItem = styled.div`
  margin-left: 20px;
  padding: 8px 0;
  font-size: 14px;
`;

const Sidebar = () => {
  const [showSubItems, setShowSubItems] = useState(false);

  const handleToggleSubItems = () => {
    setShowSubItems(!showSubItems);
  };

  return (
    <SidebarWrapper>
      <h2>Your App Name</h2>
      <SidebarItem>
        <SidebarLink href="/">Dashboard</SidebarLink>
      </SidebarItem>
      <SidebarItem>
        <SidebarLink onClick={handleToggleSubItems}>
          Analytics
        </SidebarLink>
        {showSubItems && (
          <>
            <SubItem>Subitem 1</SubItem>
            <SubItem>Subitem 2</SubItem>
          </>
        )}
      </SidebarItem>
      <SidebarItem>
        <SidebarLink onClick={handleToggleSubItems}>
          Settings
        </SidebarLink>
        {showSubItems && (
          <>
            <SubItem>Subitem 1</SubItem>
            <SubItem>Subitem 2</SubItem>
          </>
        )}
      </SidebarItem>
      {/* Adicione mais itens e subitens conforme necess√°rio */}
    </SidebarWrapper>
  );
};

export default Sidebar;
