import React, { useState } from 'react';
import { Input } from "./styles";

const Table = ({ Colunas, Data, Text }) => {
  const [formData, setFormData] = useState({
    nome: '',
    idade: '',
    cidade: '',
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSave = () => {
    // Aqui você pode implementar a lógica para salvar os dados, se necessário.
    // Por exemplo, você pode atualizar o array 'Data' com os novos dados do formulário.
    console.log('Dados salvos:', formData);
  };

  return (
    <div
      style={{
        display: 'flex',
        fontFamily: 'Arial, sans-serif',
        width: '100%',
        margin: 'auto',
        flexDirection: 'column',
      }}
    >
      <div
        style={{
          display: 'flex',
          marginBottom: '20px',
          alignItems: 'center',
          marginBottom: '1em',

        }}
      >
        {Colunas.map((coluna, index) => (
          <React.Fragment key={index}>        
            <Input
              type="text"
              id={coluna}
              name={coluna}
              placeholder={coluna}
              value={formData[coluna.toLowerCase()]}
              onChange={handleInputChange}
            />
          </React.Fragment>
        ))}

        <button onClick={handleSave} style={{ marginTop: '0px' }}>
          Salvar
        </button>
      </div>

      <table
        style={{
          width: '100%',
          borderCollapse: 'collapse',
          marginBottom: '20px',
        }}
      >
        <thead>
          <tr style={{ borderBottom: '3px solid #ccc' }}>
            {Colunas.map((coluna, index) => (
              <th key={index} style={{ padding: '10px', textAlign: 'left' }}>
                {coluna}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {Data.map((rowData, rowIndex) => (
            <tr key={rowIndex} style={{ borderBottom: '2px solid #ddd' }}>
              {rowData.map((cellData, cellIndex) => (
                <td key={cellIndex} style={{ padding: '10px' }}>
                  {cellData}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Table;
