import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const SidebarWrapper = styled.div`
  width: 250px;
  height: 100vh;
  max-height: 100vh;
  overflow-y: auto;
  background-color: #00202d;
  color: white;
  padding: 20px;
  display: ${(props) => (props.showSidebar ? 'block' : 'none')};
`;

const SidebarItem = styled.div`
  margin-bottom: 10px;
`;

const SidebarLink = styled.a`
  display: block;
  color: white;
  text-decoration: none;
  padding: 10px 0;
  font-size: 16px;
  cursor: pointer;

  &:hover {
    background-color: #555;
  }
`;

const SubItem = styled.div`
  margin-left: 20px;
  padding: 8px 0;
  font-size: 14px;
`;

const Sidebar = () => {
  const [showSubItems, setShowSubItems] = useState({});
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 1000);

  const handleToggleSubItems = (item) => {
    setShowSubItems((prev) => ({
      ...prev,
      [item]: !prev[item],
    }));
  };

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 1000);
    };

    window.addEventListener('resize', handleResize);

    // Limpe o event listener no desmonte do componente
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <div>
      {isMobile ? (
        // Renderizar menu de hambúrguer aqui
        // Exemplo: <BurgerMenu />
        // Certifique-se de implementar o componente BurgerMenu conforme suas necessidades
        null
      ) : (
        <SidebarWrapper showSidebar={!isMobile}>
          <h2>Empresa</h2>

          <SidebarItem>
            <SidebarLink onClick={() => handleToggleSubItems('analytics')}>
              Cadastro
            </SidebarLink>

            {showSubItems['analytics'] && (
              <>
                <SubItem>
                  <SidebarLink href="/classe">Classe</SidebarLink>
                </SubItem>
                {/* Adicione mais SubItems conforme necessário */}
              </>
            )}
          </SidebarItem>

          <SidebarItem>
            <SidebarLink href="/">Elástico</SidebarLink>
          </SidebarItem>
          <SidebarItem>
            <SidebarLink href="/">Agulha</SidebarLink>
          </SidebarItem>
          <SidebarItem>
            <SidebarLink href="/">Linha</SidebarLink>
          </SidebarItem>
          <SidebarItem>
            <SidebarLink href="/">Tecido</SidebarLink>
          </SidebarItem>

          <SidebarItem>
            <SidebarLink onClick={() => handleToggleSubItems('settings')}>
              Settings
            </SidebarLink>
          </SidebarItem>

          <SidebarItem>
            <SidebarLink href="/">Logout</SidebarLink>
          </SidebarItem>
        </SidebarWrapper>
      )}
    </div>
  );
};

export default Sidebar;
